mapper.update:
    UPDATE orderdetail b,handicap c set b.winAmount=(b.odds + retreat /100 -1 )* b.amount where b.handicapId = c.Id AND b.`no`=c.Tema AND b.gameType='000' and b.handicapId= #{handicapId,jdbcType=VARCHAR};
    UPDATE orderdetail b,handicap c set b.winAmount=- b.amount where b.handicapId = c.Id AND b.`no`!=c.Tema AND b.gameType='000' and b.handicapId= #{handicapId,jdbcType=VARCHAR};

    UPDATE orderdetail b,handicap c set b.winAmount=(b.odds + retreat /100 -1 )* b.amount where b.handicapId = c.Id AND b.`no` in(c.No1,c.no2,c.No3,c.no4,c.no5,c.no6) AND b.gameType='001' and b.handicapId='e4369384f7384b089689d1e75ec6acf0';
    UPDATE orderdetail b,handicap c set b.winAmount=-b.amount where b.handicapId = c.Id AND b.`no` not in(c.No1,c.no2,c.No3,c.no4,c.no5,c.no6) AND b.gameType='001' and b.handicapId='e4369384f7384b089689d1e75ec6acf0';

    UPDATE orderdetail b,handicap c set b.winAmount=(b.odds + retreat /100 -1 )* b.amount where b.handicapId = c.Id AND b.`no` =c.No1 AND b.gameType='002' and b.handicapId='e4369384f7384b089689d1e75ec6acf0';
    UPDATE orderdetail b,handicap c set b.winAmount=-b.amount where b.handicapId = c.Id AND b.`no` !=c.No1 AND b.gameType='002' and b.handicapId='e4369384f7384b089689d1e75ec6acf0';

    UPDATE orderdetail b,handicap c set b.winAmount=(b.odds + retreat /100 -1 )* b.amount where b.handicapId = c.Id AND b.`no` =c.No2 AND b.gameType='003' and b.handicapId='e4369384f7384b089689d1e75ec6acf0';
    UPDATE orderdetail b,handicap c set b.winAmount=-b.amount where b.handicapId = c.Id AND b.`no` !=c.No2 AND b.gameType='003' and b.handicapId='e4369384f7384b089689d1e75ec6acf0';

    UPDATE orderdetail b,handicap c set b.winAmount=(b.odds + retreat /100 -1 )* b.amount where b.handicapId = c.Id AND b.`no` =c.No3 AND b.gameType='004' and b.handicapId='e4369384f7384b089689d1e75ec6acf0';
    UPDATE orderdetail b,handicap c set b.winAmount=-b.amount where b.handicapId = c.Id AND b.`no` !=c.No3 AND b.gameType='004' and b.handicapId='e4369384f7384b089689d1e75ec6acf0';

    UPDATE orderdetail b,handicap c set b.winAmount=(b.odds + retreat /100 -1 )* b.amount where b.handicapId = c.Id AND b.`no` =c.No4 AND b.gameType='005' and b.handicapId='e4369384f7384b089689d1e75ec6acf0';
    UPDATE orderdetail b,handicap c set b.winAmount=-b.amount where b.handicapId = c.Id AND b.`no` !=c.No4 AND b.gameType='005' and b.handicapId='e4369384f7384b089689d1e75ec6acf0';

    UPDATE orderdetail b,handicap c set b.winAmount=(b.odds + retreat /100 -1 )* b.amount where b.handicapId = c.Id AND b.`no` =c.No5 AND b.gameType='006' and b.handicapId='e4369384f7384b089689d1e75ec6acf0';
    UPDATE orderdetail b,handicap c set b.winAmount=-b.amount where b.handicapId = c.Id AND b.`no` !=c.No5 AND b.gameType='006' and b.handicapId='e4369384f7384b089689d1e75ec6acf0';

    UPDATE orderdetail b,handicap c set b.winAmount=(b.odds + retreat /100 -1 )* b.amount where b.handicapId = c.Id AND b.`no` =c.No3 AND b.gameType='007' and b.handicapId='e4369384f7384b089689d1e75ec6acf0';
    UPDATE orderdetail b,handicap c set b.winAmount=-b.amount where b.handicapId = c.Id AND b.`no` !=c.No6 AND b.gameType='007' and b.handicapId='e4369384f7384b089689d1e75ec6acf0';

mapper.select:
    select
    
    from orderdetail
    
      
        AND handicapId = #{handicapId,jdbcType=VARCHAR}
      
      
        AND userid = #{userid,jdbcType=VARCHAR}
      
      
        AND gameType = #{gameType,jdbcType=VARCHAR}
      
    

    order by createdate ASC

      SELECT userid,QiShu historyDate,COUNT(1) orderNum,sum(amount) amount,sum(totalAmount) as totalAmount,0 as winAmount
      FROM orderdetail
      INNER  JOIN Handicap on orderdetail.handicapId= Handicap.Id
      
        
          AND userid = #{userid,jdbcType=VARCHAR}
        
        
          AND handicapId = #{handicapId,jdbcType=VARCHAR}
        
      
      GROUP  BY userid,handicapId
