    AND handicapId = #{handicapId,jdbcType=VARCHAR}
  
  
    AND userId = #{userId,jdbcType=VARCHAR}
  
  
    AND gameType = #{gameType,jdbcType=VARCHAR}

    select
    
    from orderdetail
    
      
        AND handicapId = #{handicapId,jdbcType=VARCHAR}
      
      
        AND userId = #{userId,jdbcType=VARCHAR}
      
      
        AND gameType = #{gameType,jdbcType=VARCHAR}
      
    

    order by orderNo ASC
    
      limit #{pageIndex,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

select sum(IFNULL(canWinAmount,0)) as canWinAmount,sum(IFNULL(winAmount,0)) as winAmount
from orderdetail

  
    AND handicapId = #{handicapId,jdbcType=VARCHAR}
  
  
    AND userId = #{userId,jdbcType=VARCHAR}
  
  
    AND gameType = #{gameType,jdbcType=VARCHAR}

select
od.Id,o.orderNo,u.DagudongId,u.GudongId,u.ZongdailiId,u.DailiId,u.DagudongAccount,u.GudongAccount,u.ZongdaiAccount,u.DailiAccount,u.account
,od.Retreat,od.Amount,od.Number1,od.Odds,od.gameType,o.oddset,o.description,o.createDate,
(select u1.shareUp from user u1 where u1.parentId =#{userId,jdbcType=VARCHAR}
AND u1.Id in(od.UserId ,u.DagudongId,u.GudongId,u.ZongdailiId,u.DailiId) ) * od.Amount/100 as  shareTotal
from orderdetail od
join orders  o on od.OrderId = o.id
join user u on od.userid = u.id

  
    AND od.handicapId = #{handicapId,jdbcType=VARCHAR}
  
  
    AND u.xpath LIKE #{xpath,jdbcType=VARCHAR}
  
  
    AND od.gameType = #{gameType,jdbcType=VARCHAR}
  
  
    AND Number1 = #{numbers,jdbcType=VARCHAR}
  
  
    limit #{pageIndex,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

SELECT sum(shareTotal) as shareTotal,sum(amount) as amount from (
select
od.Id,o.orderNo,u.DagudongId,u.GudongId,u.ZongdailiId,u.DailiId,u.DagudongAccount,u.GudongAccount,u.ZongdaiAccount,u.DailiAccount,u.account
,od.Retreat,od.Amount,od.Number1,od.Odds,od.gameType,o.oddset,o.description,o.createDate,
(select u1.shareUp from user u1 where u1.parentId =#{userId,jdbcType=VARCHAR}
AND u1.Id in(od.UserId ,u.DagudongId,u.GudongId,u.ZongdailiId,u.DailiId) ) * od.Amount/100 as shareTotal
from orderdetail od
join orders o on od.OrderId = o.id
join user u on od.userid = u.id

  
    AND od.handicapId = #{handicapId,jdbcType=VARCHAR}
  
  
    AND u.xpath LIKE #{xpath,jdbcType=VARCHAR}
  
  
    AND od.gameType = #{gameType,jdbcType=VARCHAR}
  
  
    AND Number1 = #{numbers,jdbcType=VARCHAR}
  
) t

