select sum(IFNULL(totalAmount,0)) as totalAmount,sum(IFNULL(canWinAmount,0)) as canWinAmount,sum(IFNULL(winAmount,0)) as winAmount
from orders

  
    AND handicapId = #{handicapId,jdbcType=VARCHAR}
  
  
    AND userid = #{userid,jdbcType=VARCHAR}
  
  
    AND gameType = #{gameType,jdbcType=VARCHAR}

SELECT handicap.id,od.userid,riqi ,COUNT(1) orderNum,sum(od.amount) amount,sum(od.canWinAmount) as canWinAmount ,sum(od.Amount) as totalAmount,sum(od.winAmount) as winAmount FROM orders INNER JOIN orderdetail od on orders.id = od.orderId INNER JOIN Handicap on orders.handicapId= Handicap.Id
AND od.userid = #{userid,jdbcType=VARCHAR}
AND od.handicapId = #{handicapId,jdbcType=VARCHAR}
GROUP BY od.userid,od.handicapId ORDER BY riqi desc

    SELECT handicap.id,od.userid,riqi ,COUNT(1) orderNum,sum(od.amount) amount,sum(od.canWinAmount) as canWinAmount
    ,sum(od.Amount) as totalAmount,sum(od.winAmount) as winAmount
    FROM orders
    INNER  JOIN orderdetail od on orders.id = od.orderId
    INNER  JOIN Handicap on orders.handicapId= Handicap.Id
    
      
        AND od.userid = #{userid,jdbcType=VARCHAR}
      
      
        AND handicap.riqi >= #{riqi,jdbcType=VARCHAR}
      
      
        AND handicap.riqi <= #{riqi2,jdbcType=VARCHAR}
      
    
    GROUP  BY od.userid,od.handicapId
    ORDER  BY riqi desc
