mapper.select:
select

from USER


    AND UserType = #{userType,jdbcType=VARCHAR}


    AND (account like #{keyword,jdbcType=VARCHAR}
    OR NAME like #{keyword,jdbcType=VARCHAR}
    )


    AND xpath like #{xpath,jdbcType=VARCHAR}


    limit #{start ,jdbcType=INTEGER} ,10

select count(1)
from USER


    AND UserType = #{userType,jdbcType=VARCHAR}


    AND (account like #{keyword,jdbcType=VARCHAR}
    OR NAME like #{keyword,jdbcType=VARCHAR}
    )


    AND xpath like #{xpath,jdbcType=VARCHAR}

SELECT IFNULL(max(xseq),0)+1 from USER WHERE
parentId =#{parentID ,jdbcType=VARCHAR}
parentId IS NULL

SELECT IFNULL(sum(Credits),0) from USER
